        transform: translate(15px, -15px)
    }

    .learn-card-body {
        padding: 20px !important;
    }

    .learn-card-footer {
        padding: 20px;
        text-transform: uppercase;
    }

    .learn-card .learn-card-body {
        padding: 0px !important;
        padding-left: 20px;
        padding-right: 20px;
        font-size: 12px;
    }

    .learn-card .learn-card-body p {
        padding: 20px !important;
        font-size: 13px;
        height: 100px;
    }

    .learn-card .learn-card-body ul {
        height: 100px;
        padding: 20px !important;
        font-size: 11px;
        padding-bottom: 0px;
    }

    .button {
        border-width: 0;
        cursor: pointer;
        text-align: center;
        text-decoration: none;
        display: inline-block;
        background-color: #f9423a;
        border-color: #424242;
        color: #fff;
        outline: 0;
        vertical-align: middle;
        border-radius: 0px;
        box-shadow: 3px 3px 7px #AAA;
        width: 42%;
        padding: 10px;
        padding-top: 15px;
        padding-bottom: 15px;
        font-family: Interstate;
        margin: 10px;
        font-size: 13px !important;
    }

    .course {
        text-align: left;
        box-shadow: 3px 3px 7px #a66;
        width: 95%;
        padding: 1%;
        margin-top: 20px;
        border: 1px solid #AAA;
        background-color: #fff;
        margin-bottom: 20px;
        margin-right: 11%;
        margin-left: 11%;
    }


    .course h3 {
        box-shadow: 3px 3px 7px #a66;
        text-transform: uppercase;
        color: #eceff1;
        background-color: #f9423a;
        text-align: center;
        margin: 0;
        margin: 10px;
        padding: 20px;
        color: white;

    }

    .course .scourse {
        transition: all 1s ease;
        box-shadow: 1px 1px 7px #a66;
        /* float: left; */
        margin-right: 0px;
        border: 1px solid #AAA;
        width: 338px;
        height: 372px;
        background-color: #fff;
        position: relative;
        margin: 10px;
        text-decoration: none;
        display: inline-block;
    }

    .course .scourse:hover {
