        transform: translate(5px, -5px)
    }

    .course .scourse strong {
        text-transform: uppercase;
        background-color: #f9423a;
        position: absolute;
        color: black;
        text-align: center;
        top: 218px;
        color: white;
        height: 25px;
        left: 0px;
        width: 100%;
        font-size: 15px;
        padding: 18px;
        line-height: 0;
        margin-top: 0px;
    }

    .course .scourse p {
