    }

    .flex-33 {
        padding: 20px;
        flex-basis: 24.33%;

    }








    #cover {
        position: fixed;
        height: 100%;
        width: 100%;
        top: 0;
        left: 0;
        background: #000;
        z-index: 9999;
    }


    .epoxy-calculator {
        position: absolute;
        max-width: 1440px;
        top: 170px;
        width: 620px;
        margin: auto;
        display: -webkit-box;
        display: -moz-box;
        display: -ms-flexbox;
        display: -webkit-box;
        display: -webkit-flexbox;
        display: -webkit-flex;
        /* display: flex; */
