        -moz-transition: all 0.3s ease-out;
        -ms-transition: all 0.3s ease-out;
        transition: all 0.3s ease-out
    }

    .epoxy-calc-popup.popup-active {
        visibility: visible;
        opacity: 1;
        display: -webkit-box;
        display: -moz-box;
        display: -ms-flexbox;
        display: -webkit-box;
        display: -webkit-flexbox;
        display: -webkit-flex;
        display: flex
    }

    .epoxy-calc-popup .epoxy-calculator {
        padding: 30px 25px;
        background: #fff;
        width: 625px;
        position: relative
    }

    #player {
        box-shadow: 3px 4px 6px #AAA;
    }

    .epoxy-calc-popup .close-btn {
        background: #fff;
        color: #000;
        position: absolute;
        top: 0;
        right: 0;
        padding: 10px;
        display: -webkit-box;
        display: -moz-box;
        display: -ms-flexbox;
        display: -webkit-box;
        display: -webkit-flexbox;
        display: -webkit-flex;
